        <script type="text/javascript">
            var timer;
            $(document).on("click","#btnSubmit",function (){
                if ($("#up_parent_tel").val() === ""){
                    $("#up_parent_tel").focus();
                    return;
                }

                if ($("input[name='up_student_id']:checked").length <= 0){
                    $(".alert").removeClass("d-none");
                    timer = setInterval(function (){
                        hideAlert();
                        console.log("timer");
                    },3000);
                    return;
                }

                $("#preFrm").submit();
            });

            function hideAlert(){
                $(".alert").addClass("d-none");
                clearInterval(timer);
            }
        </script>
